import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
  name: 'maskPlaca',
  standalone: true
})
export class MaskPlacaPipe implements PipeTransform {
  transform(value: string): string {
    if (!value) return '';

    // 1. Remove qualquer tra√ßo ou espa√ßo existente e deixa em mai√∫sculo
    const limpo = value.toUpperCase().replace(/[^A-Z0-9]/g, '');

    // 2. Se tiver 7 caracteres (padr√£o Brasil), coloca o tra√ßo ap√≥s as 3 letras
    if (limpo.length === 7) {
      return `${limpo.slice(0, 3)}-${limpo.slice(3)}`;
    }

    return value;
  }
}

@Pipe({
  name: 'maskCpf',
  standalone: true
})
export class MaskCpfPipe implements PipeTransform {

  transform(value: string | null | undefined): string {
    if (!value) return '';

    const limpo = value.replace(/\D/g, '');

    if (limpo.length !== 11) return value;

    return limpo.replace(
      /(\d{3})(\d{3})(\d{3})(\d{2})/,
      '$1.$2.$3-$4'
    );
  }
}

